/* ==UserStyle==
@name         github-monday
@author       Karl_255
@namespace    github.com/Karl255
@version      1.0.4
@description  Changes the GitHub contribution graph's first day of week on user profiles to Monday instead of Sunday.
@homepageURL  https://github.com/Karl255/UserCSS-Styles
@supportURL   https://github.com/Karl255/UserCSS-Styles/issues
@license      unlicense
@preprocessor default
==/UserStyle== */

@-moz-document domain("github.com") {
	/* when the activity overview is enabled the grid rects are 10px * 10px (3px spacing) */
	.ContributionCalendar-grid[style*="border-spacing: 3px"] {
		--dist: 13px;
	}

	/* but when it's disabled, they're 11px * 11px (4px spacing) */
	.ContributionCalendar-grid[style*="border-spacing: 4px"] {
		--dist: 15px;
	}

	/* all days up a little */
	.ContributionCalendar-grid tbody tr {
		transform: translateY(calc(var(--dist) * -1));
	}

	/* all sundays all the way down */
	.ContributionCalendar-grid tbody tr:first-child {
		transform: translateY(calc(var(--dist) * 6));
	}

	/* hide first sunday (otherwise it'll be outside the box) */
	.ContributionCalendar-grid tbody tr:first-child .ContributionCalendar-label + * {
		display: none;
	}

	/* show sunday text */
	.ContributionCalendar-grid tbody tr:first-child .ContributionCalendar-label span:last-child  {
		clip-path: unset !important;
	}
}

/* ==UserStyle==
@name         GitHub Retro
@version      0.1.4
@description  Retrofit the repository interface prior to June 2020 GitHub update
@namespace    ashwinvis
@author       ashwinvis
@homepageURL  https://github.com/ashwinvis/github-retro
@supportURL   https://github.com/ashwinvis/github-retro/issues
@license      CC-BY-SA-4.0
@preprocessor uso
==/UserStyle== */

@-moz-document regexp("^https?:\\/\\/github.com.*") {
	:root {
		--line-color: rgba(0, 0, 0, 0.1);
	}

	@media (min-width: 1280px) {
		div.js-global-bar {
			max-width: 1310px;
			margin-left: auto;
			margin-right: auto;
		}
		nav.UnderlineNav {
			max-width: 1280px;
			margin-left: auto;
			margin-right: auto;
		}
		/* Horizontal line under header */
		main > div:nth-child(1),
		header.orghead > div:nth-child(2) {
			box-shadow: inset 0 -1px 0 var(--line-color) !important;
		}
		/*
		At desktop resolutions some of the repo navigation items disappear. This fixes it.
		Uses !important because it appears to be controlled by a resize event handler.
		Possibly a GitHub bug.
		*/
		a.js-selected-navigation-item {
			visibility: unset !important;
		}
		/* Hides overflow button when not needed */
		.js-responsive-underlinenav-overflow{
		    display: none;
		}
	}
}
@-moz-document domain("github.com") {
	/* Squarish avatars. */
	.avatar-user {
		border-radius: 10% !important;
	}
}

